mapper.select:
select A.id
from
cobradores A where A.id = #{id}

select A.id, A.persona_id, B.id, B.nombres, B.apellidos, B.dni, B.telefono, B.direccion, B.correo, B.fecha_nacimiento, B.estado, B.fecha_creacion, B.creado_por, B.fecha_modificacion, B.modificado_por, B.eliminado_por from cobradores A , personas B
CONCAT(nombres,' ',apellidos) LIKE CONCAT('%',#{search},'%')
ORDER BY ${sort} ${order}
