mapper.update:
Update comerciantes
set
ruc = #{ruc},
razon_social = #{razon_social}
where id = #{id}

mapper.select:
select * from( select A.id,A.ruc,A.razon_social,A.personas_id, B.nombres, B.apellidos, B.dni, B.telefono, B.direccion, B.correo, B.fecha_nacimiento, B.estado, B.fecha_creacion, B.creado_por, B.fecha_modifcacion, B.modifcado_por, B.eliminado_por from comerciantes A , personas B where A.personas_id = B.id) T
apellidos LIKE CONCAT(#{search},'%')
ORDER BY ${sort} ${order}
