mapper.update:
Update series
set estado = 0
where id = #{id}

mapper.select:
select A.id,A.codigo,A.descripcion,A.conceptos_id,A.mercados_id,A.correlativo, A.estado,A.fecha_creacion,A.creado_por, A.fecha_modifcacion,A.modifcado_por, A.eliminado_por,B.descripcion desConcepto,C.descripcion desMercado from series A inner join conceptos B on B.id = A.conceptos_id inner join mercados C on C.id = A.mercados_id
CONCAT(A.descripcion) LIKE CONCAT(#{search},'%')
ORDER BY ${sort} ${order}
