mapper.select:
select count(*) from tickets
mercados_id = #{mercados_id}
AND cast(fecha_ticket as date) = cast(#{fecha_ticket} as date)

mapper.update:
Update tickets
set estado = 0
where id = #{id}

Update tickets
set pagado = 1,
estado_visita = #{estado_visita}
where id = #{id}

Update tickets
set estado_visita = #{estado_visita},
 observaciones = #{observaciones},
 fecha_obs = #{fecha_obs}
where id = #{id}

mapper.select:
select A.id, B.id puestos_id, B.codigo codigo_puesto, A.mercados_id, E.descripcion descripcion_mercado, B.conceptos_id, F.descripcion descripcion_concepto, A.ubicaciones_id, G.descripcion descripcion_ubicacion, B.giro_id, H.valor_cadena1 descripcion_giro, A.comerciantes_id, D.dni, D.nombres, D.apellidos, D.correo, D.telefono, A.estado, A.pagado, A.fecha_ticket, A.fecha_creacion, A.estado_visita, I.valor_cadena1 descripcion_visita, A.observaciones from tickets A inner join puestos B on B.id = A.puestos_id left join comerciantes C on C.id = A.comerciantes_id inner join personas D on D.id = C.personas_id inner join mercados E on E.id = A.mercados_id inner join conceptos F on F.id = B.conceptos_id inner join ubicaciones G on G.id = A.ubicaciones_id inner join parametros H on H.codigo=2 and H.subcodigo = B.giro_id inner join parametros I on I.codigo=3 and I.valor_entero = A.estado_visita
A.pagado = #{pagado}
AND cast(A.fecha_ticket as date) >= #{fecha_incio} and #{fecha_fin} >= cast(A.fecha_ticket as date)
AND B.codigo_qr = #{codigo_qr}
AND A.comerciantes_id = #{comerciantes_id}
AND A.mercados_id = #{mercados_id}
AND A.ubicaciones_id = #{ubicaciones_id}
ORDER BY ${sort} ${order}
