mapper.select:
select A.id, A.descripcion, A.estado, A.fecha_creacion, A.creado_por, A.fecha_modifcacion, A.modifcado_por, A.eliminado_por from ubicaciones A
CONCAT(A.descripcion) LIKE CONCAT(#{search},'%')
ORDER BY ${sort} ${order}

mapper.update:
Update ubicaciones
set estado = 0
where id = #{id}
