mapper.update:
Update usuarios
 set activo = 0
 where usuario = #{username}

Update usuarios
 set activo = 0
 where id = #{idUsuario}

      Update usuarios
      set 
      usuario = #{usuario},
      password = #{password}, fechaUltAcc = #{fechaUltAcc},
	  activo = #{activo}, perfiles_codigo = #{perfiles_codigo}
      where usuario = #{usuario}

      Update usuarios
      set 
      usuario = #{usuario},
      password = #{password}, fechaUltAcc = #{fechaUltAcc},
	  activo = #{activo}, perfiles_codigo = #{perfiles_codigo}
      where id = #{idUsuario}

mapper.select:
select A.id, A.usuario, A.password, A.fechaUltAcc, A.perfiles_codigo, A.personas_id, A.activo, B.id, B.nombres, B.apellidos, B.dni, B.telefono, B.direccion, B.correo, B.fecha_nacimiento, B.estado, B.fecha_creacion, B.creado_por, B.fecha_modifcacion, B.modifcado_por, B.eliminado_por, C.mercados_id, D.nombre nombre_perfil from usuarios A inner join personas B on A.personas_id = B.id left join cobradores C on C.personas_id = B.id inner join perfiles D on A.perfiles_codigo = D.id
and CONCAT(apellidos) LIKE CONCAT(#{search},'%')
ORDER BY ${sort} ${order}
